export default {
	allowUpdate: () => !!configured_tests.selectedRows.length,
	getIds: () => {
		return `(${configured_tests.selectedRows.map(item => `\'${item.test_id}\'`).join(", ")})`
	},
	bulkUpdate: () => {
		let test_activated_b = test_activated_bulk.isChecked;
		Bulk_Update_Test_Activated.run(() => {
			showAlert('Successfully updated test activation status');
			closeModal('Activate_Tests_Modal');
			resetWidget('test_activated_bulk');
			Configured_tests_data.run();
		}, () => {}, {'test_activated_bulk': test_activated_b, 'test_ids': this.getIds()});
	},
	bulkDelete: () => {
		Bulk_Delete_Tests.run(() => {
			showAlert('Successfully deleted tests');
			closeModal('Delete_Tests_Modal');
			Configured_tests_data.run();
		}, () => {}, {'test_ids': this.getIds()});
	},
	// Flag to say if a test_type uses test_parameters in configured_tests
	testUsesParameters: (test_type_in) => {
		const test_types = Test_Types_That_Use_Parameters.data.map(item => { return item.test_type;});
		return test_types.includes(test_type_in);
	},
	// Flag to say if a test_type uses columns in configured_tests
	testUsesColumn: (test_type_in) => {
		const test_types = Test_Types_That_Use_Column.data.map(item => { return item.test_type;});
		return test_types.includes(test_type_in);
	},

}

